INSERT IGNORE INTO `#__content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(1, 36, 'About', 'about', '<p class="intro last">This tells you a bit about this project. When you are <a href="index.php/home">logged in</a> you will be able to create and edit articles on the front end without accessing the administration area.</p>', '', 1, 2, '2011-01-01 00:00:01', 306, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-01-04 16:10:42', '0000-00-00 00:00:00', '{"image_intro":"","image_intro_alt":"","image_intro_caption":"","float_intro":"","image_fulltext":"images\\/headers\\/walden-pond.jpg","image_fulltext_alt":"","image_fulltext_caption":"","float_fulltext":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 2, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(3, 38, 'Editor', 'editor', '<p class="intro">Editing articles from the front end. New articles in category Cases will be automatically added to the bottom menu.</p>\r\n<h3>Content</h3>\r\n<ul>\r\n<li>Title – header title</li>\r\n<li>Alias – used in the URL address only for the new article</li>\r\n<li>Styles: body, last, h3, h2</li>\r\n<li>Table – insert and edit credits-table</li>\r\n<li>Gallery – insert gallery</li>\r\n<li>Article – insert link to another article</li>\r\n<li>Image – insert image, video or presentation</li>\r\n</ul>\r\n<h3>Gallery</h3>\r\n<p>{gallery}sampledata/fruitshop{/gallery}</p>\r\n<ul>\r\n<li>Click Gallery, select the folder, e.g. \'sampledata/fruitshop\'</li>\r\n<li>Aditional dtm-template, e.g. \'sampledata/fruitshop,dtm\'</li>\r\n<li>Optional add captions, by creating e.g. fruitshop.txt in \'sampledata/fruitshop\' with file names and captions separated by \'|\'</li>\r\n</ul>\r\n<h3>Images</h3>\r\n<ul>\r\n<li>Intro Image – bottom menu image</li>\r\n<li>Full article image – header image or video</li>\r\n<li>Alt text – Aditional CSS formatting of the header</li>\r\n</ul>', '', 1, 9, '2011-01-05 00:00:01', 306, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-01-05 16:55:36', '0000-00-00 00:00:00', '{"image_intro":"images\\/sampledata\\/parks\\/landscape\\/180px_ormiston_pound.jpg","image_intro_alt":"","image_intro_caption":"","float_intro":"","image_fulltext":"images\\/sampledata\\/parks\\/landscape\\/800px_ormiston_pound.jpg","image_fulltext_alt":"","image_fulltext_caption":"","float_fulltext":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"0","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 2, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(4, 39, 'Styles', 'styles', '<p class="intro">You can use the following CSS classes to add additional formatting to your articles.</p>\r\n<table class="table">\r\n<thead>\r\n<tr>\r\n<td>Class</td>\r\n<td>Description</td>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>intro</td>\r\n<td>paragraph as h2</td>\r\n</tr>\r\n<tr>\r\n<td>last</td>\r\n<td>paragraph bottom margin: 0, credits-table border: 0</td>\r\n</tr>\r\n<tr>\r\n<td>credits</td>\r\n<td>table</td>\r\n</tr>\r\n<tr>\r\n<td>tracklist</td>\r\n<td>description list</td>\r\n</tr>\r\n<tr>\r\n<td>wide767</td>\r\n<td>image or gallery for &lt;768</td>\r\n</tr>\r\n<tr>\r\n<td>image-container</td>\r\n<td>container for the images</td>\r\n</tr>\r\n<tr>\r\n<td>embed-container</td>\r\n<td>container 16:9</td>\r\n</tr>\r\n<tr>\r\n<td>embed-container-*by*</td>\r\n<td>ratio adjustment of the embed-container 17:10, 5:3, 16:10, 3:2, 14:10, 4:3, 1:1, 3:4, 10:14, 210:297, 2:3, 3:5</td>\r\n</tr>\r\n<tr>\r\n<td>embed-item</td>\r\n<td>embedded object, e.g. iframe for vimeo or issuu, 100% width/height</td>\r\n</tr>\r\n<tr>\r\n<td>embed-image</td>\r\n<td>embedded image, as in the gallery, 100% maximum width/height, center/center</td>\r\n</tr>\r\n<tr>\r\n<td>top-*</td>\r\n<td>top margin 0–12 interlines</td>\r\n</tr>\r\n<tr>\r\n<td>bottom-*</td>\r\n<td>bottom margin 0–12 interlines</td>\r\n</tr>\r\n<tr>\r\n<td>span*</td>\r\n<td>columns 1–12 for 980–1199, &lt;768</td>\r\n</tr>\r\n<tr>\r\n<td>offset*</td>\r\n<td>offsets 1–12 for 980–1199, &lt;768</td>\r\n</tr>\r\n<tr>\r\n<td>col-sm-*</td>\r\n<td>columns 1–12 for 768–979</td>\r\n</tr>\r\n<tr>\r\n<td>col-sm-offset-*</td>\r\n<td>offsets 1–12 for 768–979</td>\r\n</tr>\r\n<tr>\r\n<td>col-lg-*</td>\r\n<td>columns 1–12 for 1200–1599</td>\r\n</tr>\r\n<tr>\r\n<td>col-lg-offset-*</td>\r\n<td>offsets 1–12 for 1200–1599</td>\r\n</tr>\r\n<tr>\r\n<td>col-hd-*</td>\r\n<td>columns 1–12 for &gt;1600</td>\r\n</tr>\r\n<tr>\r\n<td>col-hd-offset-*</td>\r\n<td>offsets 1–12 for &gt;1600</td>\r\n</tr>\r\n</tbody>\r\n</table>', '', 1, 9, '2011-01-03 00:00:01', 306, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-01-03 00:00:00', '0000-00-00 00:00:00', '{"image_intro":"images\\/sampledata\\/parks\\/landscape\\/250px_cradle_mountain_seen_from_barn_bluff.jpg","image_intro_alt":"","image_intro_caption":"","float_intro":"","image_fulltext":"images\\/sampledata\\/parks\\/landscape\\/800px_cradle_mountain_seen_from_barn_bluff.jpg","image_fulltext_alt":"","image_fulltext_caption":"","float_fulltext":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"0","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 1, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(5, 40, 'Resources', 'resources', '<p class="intro">Links to useful online resources collected while working on this project.</p>\r\n<h3>Joomla</h3>\r\n<table class="table">\r\n<tbody>\r\n<tr>\r\n<td><a href="https://github.com/joomla/joomla-cms/releases/">joomla</a></td>\r\n<td>content management system</td>\r\n</tr>\r\n<tr>\r\n<td><a href="https://www.akeebabackup.com/downloads/akeeba-kickstart.html">kickstart</a></td>\r\n<td>archive extraction tool</td>\r\n</tr>\r\n<tr>\r\n<td><a href="https://www.akeebabackup.com/downloads/akeeba-backup.html">backup</a></td>\r\n<td>full site backup and transfer component.</td>\r\n</tr>\r\n<tr>\r\n<td><a href="https://www.akeebabackup.com/download/official/remote-cli.html">remote</a></td>\r\n<td>command-line PHP application which allows you to update Akeeba Backup as well as take, list and download backups</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://www.nonumber.nl/extensions/dbreplacer">dbreplacer</a></td>\r\n<td>search and replace in any table in your database</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://www.nonumber.nl/extensions/contenttemplater">contenttemplater</a></td>\r\n<td>create predefined reusable content templates</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://www.giantleaplab.com/our-software/true-typography">true-typography</a></td>\r\n<td>hyphenation, spacing control, character replacement</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://www.fieldsattach.com/">fieldsattach</a></td>\r\n<td>attach all kinds of objects to a article or/and category</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://gtranslate.net/features">gtranslate</a></td>\r\n<td>translations between 81 languages</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://www.eshiol.it/joomla/j2xml/j2xml-3.html">j2xml</a></td>\r\n<td>import/export content</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>Scripts</h3>\r\n<table class="table">\r\n<tbody>\r\n<tr>\r\n<td><a href="http://adaptive-images.com/">adaptive-images</a></td>\r\n<td>automatically creates, caches, and delivers device appropriate rescaled versions of your web page \'s images; optional configuration $resolutions = array(2560, 2048, 1920, 1680, 1440, 1280, 1024, 768, 480, 320); $jpg_quality = 60; $sharpen = false;</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://hammerjs.github.io/">hammer.js</a></td>\r\n<td>recognize gestures made by touch, mouse and pointer</td>\r\n</tr>\r\n<tr>\r\n<td><a href="https://github.com/vimeo/player-api/tree/master/javascript">froogaloop.js</a></td>\r\n<td>control the vimeo embedded player</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://eduardomb.github.io/scroll-up-bar/">scroll-up-bar.js</a></td>\r\n<td>hides the top bar when scrolling down, and show it when scrolling up</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://fgnass.github.io/spin.js/">spin.js</a></td>\r\n<td>dynamically creates spinning activity indicators</td>\r\n</tr>\r\n<tr>\r\n<td><a href="https://github.com/paulirish/infinite-scroll">infinitescroll.js</a></td>\r\n<td>pre-fetching content from a subsequent page</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>Tools</h3>\r\n<table class="table">\r\n<tbody>\r\n<tr>\r\n<td><a href="http://alefeuvre.github.io/foundation-grid-displayer/">grid-displayer</a></td>\r\n<td>bookmarklet which displays the grid for Bootstrap</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://keyes.ie/things/baseliner/">baseliner</a></td>\r\n<td>overlay a baseline grid on any webpage</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://placehold.it/">placehold.it</a></td>\r\n<td>quick and simple image placeholders</td>\r\n</tr>\r\n<tr>\r\n<td>Bootstrap UI Editors</td>\r\n<td><a href="http://bootswatchr.com/">bootswatchr</a>, <a href="https://www.boottheme.com/">boottheme</a>, <a href="http://bootply.com/">bootply</a>, <a href="http://brix.io/">brix.io</a>, <a href="http://v4.pingendo.com/playground.html">pingendo</a></td>\r\n</tr>\r\n<tr>\r\n<td>Design-In-Browser</td>\r\n<td><a href="http://froont.com/">froont</a>, <a href="http://webflow.com/">webflow</a>, <a href="http://stackhive.com/">stackhive</a>, <a href="http://webydo.com/">webydo</a>, <a href="http://www.silex.me/">silex</a>, <a href="https://wordpress.org/">wordpress</a>+<a href="https://themifyflow.com/">themifyflow</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>Snippets</h3>\r\n<table class="table">\r\n<tbody>\r\n<tr>\r\n<td><a href="http://www.gridlover.net/">gridlover</a></td>\r\n<td>sizing type tidy and logical</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://easings.net/">easings</a></td>\r\n<td>easing functions cheat sheet</td>\r\n</tr>\r\n<tr>\r\n<td><a href="https://iframely.com/">iframely</a></td>\r\n<td>responsive embed codes API</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://browserhacks.com/">browserhacks</a></td>\r\n<td>list of CSS/JS browser hacks from all over the interwebs</td>\r\n</tr>\r\n<tr>\r\n<td><a href="http://demo.w3developer.de/">demo.w3developer.de</a></td>\r\n<td>joomla, bootstrap, javascript snippets</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>Documentation</h3>\r\n<ul>\r\n<li><a href="http://docs.joomla.org/">Joomla! Documentation</a></li>\r\n<li><a href="http://docs.joomla.org/Template_Development">Joomla! Template Development</a></li>\r\n<li><a href="http://docs.joomla.org/How_to_override_the_output_from_the_Joomla!_core">Joomla! Override core output</a></li>\r\n<li><a href="http://docs.joomla.org/J3.x:Access_Control_List_Tutorial">Joomla! ACL</a></li>\r\n<li><a href="http://api.joomla.org/">Joomla! API</a></li>\r\n<li><a href="http://www.tinymce.com/wiki.php/Configuration3x">TinyMCE Configuration</a></li>\r\n<li><a href="http://www.tinymce.com/wiki.php/API3:tinymce.api.3.x">TinyMCE API</a></li>\r\n<li><a href="http://andmag.se/2011/11/responsive-embeds/">Responsive embeds</a></li>\r\n<li><a href="http://andmag.se/2012/10/responsive-images-how-to-prevent-reflow/">Responsive images</a></li>\r\n<li><a href="http://developer.vimeo.com/player/js-api">Vimeo Developer API</a></li>\r\n<li><a href="http://lesscss.org/">Less CSS pre-processor</a></li>\r\n<li><a href="http://getbootstrap.com/2.3.2/">Bootsrap 2.3.2</a></li>\r\n</ul>', '', 1, 9, '2010-12-31 00:00:01', 306, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-12-31 00:00:01', '0000-00-00 00:00:00', '{"image_intro":"images\\/sampledata\\/parks\\/landscape\\/120px_pinnacles_western_australia.jpg","image_intro_alt":"","image_intro_caption":"","float_intro":"","image_fulltext":"images\\/sampledata\\/parks\\/landscape\\/800px_pinnacles_western_australia.jpg","image_fulltext_alt":"","image_fulltext_caption":"","float_fulltext":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"0","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 0, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(6, 43, 'Template', 'template', '<p class="intro">This describes the template that controls the overall look and layout of this site.</p>\r\n<h3 id="grid">Grid</h3>\r\n<p class="last">height: line 30, interline 15; page width: 12 columns + 11 intercolumns, centered</p>\r\n<table class="table">\r\n<tbody>\r\n<tr>\r\n<td>Screen</td>\r\n<td>Column</td>\r\n<td>Intercolumn</td>\r\n<td>Page width</td>\r\n<td>Notes</td>\r\n</tr>\r\n<tr>\r\n<td>&lt;768</td>\r\n<td> </td>\r\n<td> </td>\r\n<td>auto</td>\r\n<td>margins l/r 1em</td>\r\n</tr>\r\n<tr>\r\n<td>&gt;768</td>\r\n<td>27</td>\r\n<td>30</td>\r\n<td>654</td>\r\n<td> </td>\r\n</tr>\r\n<tr>\r\n<td>&gt;980</td>\r\n<td>46</td>\r\n<td>30</td>\r\n<td>882</td>\r\n<td>default</td>\r\n</tr>\r\n<tr>\r\n<td>&gt;1200</td>\r\n<td>52</td>\r\n<td>30</td>\r\n<td>954</td>\r\n<td> </td>\r\n</tr>\r\n<tr>\r\n<td>&gt;1600</td>\r\n<td>70</td>\r\n<td>30</td>\r\n<td>1170</td>\r\n<td> </td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 id="font">Font</h3>\r\n<p class="last">\'Roboto\' 300; h3: 400; h1, h2: 100</p>\r\n<table class="table">\r\n<tbody>\r\n<tr>\r\n<td>Screen</td>\r\n<td>small</td>\r\n<td>body</td>\r\n<td>h3</td>\r\n<td>h2, intro</td>\r\n<td>h1</td>\r\n</tr>\r\n<tr>\r\n<td>&gt;1600</td>\r\n<td>12</td>\r\n<td>19</td>\r\n<td>21</td>\r\n<td>42</td>\r\n<td>130</td>\r\n</tr>\r\n<tr>\r\n<td>&gt;980</td>\r\n<td>12</td>\r\n<td>19</td>\r\n<td>21</td>\r\n<td>40/1.5</td>\r\n<td>110/1</td>\r\n</tr>\r\n<tr>\r\n<td>&gt;768</td>\r\n<td>12</td>\r\n<td>19</td>\r\n<td>21</td>\r\n<td>40</td>\r\n<td>80</td>\r\n</tr>\r\n<tr>\r\n<td>&lt;768</td>\r\n<td>12</td>\r\n<td>19</td>\r\n<td>21</td>\r\n<td>29</td>\r\n<td>49/1.2</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 id="color">Color</h3>\r\n<table class="table">\r\n<tbody>\r\n<tr>\r\n<td>#f3f3f4</td>\r\n<td>background</td>\r\n</tr>\r\n<tr>\r\n<td>#373749</td>\r\n<td>text</td>\r\n</tr>\r\n<tr>\r\n<td>#d9e3e3</td>\r\n<td>hover, borders, selection background</td>\r\n</tr>\r\n<tr>\r\n<td>white</td>\r\n<td>top menu background over the main text area</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 id="transition">Transition</h3>\r\n<p class="last">0.4s ease</p>\r\n<table class="table">\r\n<tbody>\r\n<tr>\r\n<td>Property</td>\r\n<td>Element</td>\r\n</tr>\r\n<tr>\r\n<td>color</td>\r\n<td>link-text</td>\r\n</tr>\r\n<tr>\r\n<td>transparency</td>\r\n<td><a href="#gallery">gallery</a>, <a href="#bmenu">bottom menu</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 id="tmenu">Top menu</h3>\r\n<ul>\r\n<li>width 100%, height 30</li>\r\n<li>margins: 30, &lt;768 1em</li>\r\n<li>(dis)appears on scroll down/up</li>\r\n<li>background change on scroll to/from main text area(js)</li>\r\n<li>pager: left/right arrows, ip_iconsregular 30px, right</li>\r\n</ul>\r\n<h3 id="bmenu">Bottom menu</h3>\r\n<ul>\r\n<li>width 100%, heading as <a href="#font">h3</a></li>\r\n<li>active/hover item transparency 7%</li>\r\n<li>2 templates: icons block margins top/bottom 75, icons left</li>\r\n<li>default template: width as <a href="#grid">page width</a>, icons block margin left 30,</li>\r\n<li>icon columns &lt;768 1, &gt;768 3, &gt;980 4 margins left/bottom 30</li>\r\n<li>xwidth ­template: icons block max. width 2400 ~(screen width / 300) * 300 (js) center, icons 270x270 margins left/right 15 bottom 30</li>\r\n</ul>\r\n<h3 id="head">Header</h3>\r\n<ul>\r\n<li>width 100%, height 80%</li>\r\n<li>background: image, center/center, size cover – scale the background image to be as large as possible so that the background area is completely covered by the background image.</li>\r\n<li><a href="#font">h1</a> as <a href="#grid">page width</a>; margin bottom: 105, &lt;768: 60; transparency change on scroll(js)</li>\r\n<li>additional css formatting from the editor</li>\r\n</ul>\r\n<h3 id="article">Article</h3>\r\n<ul>\r\n<li>width as <a href="#grid">page width</a></li>\r\n<li>margin top: 105, &lt;768 60; margin bottom: 90</li>\r\n<li>text, image, video, gallery: margin bottom 105 except last</li>\r\n<li>body text, tracklist: width &gt;1200 41em</li>\r\n<li>tracklist: description margin bottom 30 except last</li>\r\n<li>credits: columns 50%, padding 15 except left, border bottom except last</li>\r\n</ul>\r\n<h3 id="gallery">Gallery</h3>\r\n<ul>\r\n<li>width 100%, &lt;768 margins left/right 1em</li>\r\n<li>aspect ratio: 16:9, &lt;980 narrow gallery 1:1</li>\r\n<li>image: center/center, margin bottom 90</li>\r\n<li>caption: height 30, position image bottom margin 0</li>\r\n<li>controls: height 45, position gallery bottom 30</li>\r\n<li>control buttons: previous/next, dtm: desktop/tablet/mobile</li>\r\n</ul>', '', 1, 9, '2011-01-02 00:00:01', 306, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2011-01-02 00:00:01', '0000-00-00 00:00:00', '{"image_intro":"images\\/sampledata\\/parks\\/landscape\\/120px_rainforest_bluemountainsnsw.jpg","image_intro_alt":"","image_intro_caption":"","float_intro":"","image_fulltext":"images\\/sampledata\\/parks\\/landscape\\/727px_rainforest_bluemountainsnsw.jpg","image_fulltext_alt":"","image_fulltext_caption":"","float_fulltext":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"0","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 0, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
